#!/bin/bash
# Third-party script: wireguard-install
# Category: security
# Description: WireGuard VPN server installer
# Source: https://raw.githubusercontent.com/angristan/wireguard-install/master/wireguard-install.sh
# Generated by eXtensibleSH workflow

set -euo pipefail

# Colors for output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
NC='\033[0m' # No Color

echo -e "${GREEN}Installing wireguard-install...${NC}"
echo -e "${YELLOW}Description: WireGuard VPN server installer${NC}"
echo -e "${YELLOW}Source: https://raw.githubusercontent.com/angristan/wireguard-install/master/wireguard-install.sh${NC}"
echo

# Download and execute with error handling
if command -v curl >/dev/null 2>&1; then
    echo -e "${GREEN}Downloading and executing installation script...${NC}"
    if curl -fsSL "https://raw.githubusercontent.com/angristan/wireguard-install/master/wireguard-install.sh" | bash; then
        echo -e "${GREEN}✓ wireguard-install installation completed successfully!${NC}"
    else
        echo -e "${RED}✗ Installation failed. Please check the source URL manually.${NC}"
        echo -e "${YELLOW}Manual installation: https://raw.githubusercontent.com/angristan/wireguard-install/master/wireguard-install.sh${NC}"
        exit 1
    fi
elif command -v wget >/dev/null 2>&1; then
    echo -e "${GREEN}Downloading and executing installation script...${NC}"
    if wget -qO- "https://raw.githubusercontent.com/angristan/wireguard-install/master/wireguard-install.sh" | bash; then
        echo -e "${GREEN}✓ wireguard-install installation completed successfully!${NC}"
    else
        echo -e "${RED}✗ Installation failed. Please check the source URL manually.${NC}"
        echo -e "${YELLOW}Manual installation: https://raw.githubusercontent.com/angristan/wireguard-install/master/wireguard-install.sh${NC}"
        exit 1
    fi
else
    echo -e "${RED}✗ Neither curl nor wget found. Please install one of them first.${NC}"
    exit 1
fi
